package applicationDay1;

public class Vowels {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char vowel = 'A';
		
		switch(vowel) {
			case 'A' :
			case 'I' :
			case 'O' :
			case 'U' :
				System.out.println("Bold vowel");
				break;
				default:
					System.out.println("Thin Vowels");
				
		}
	}

}
